{
y &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1.0&lt;/span&gt; / (&lt;span class="hljs-number"&gt;2&lt;/span&gt; * i &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;);
}
