&lt;p&gt;可以使用Scanner类的nextLine()获取一行输入到字符串&amp;#xff0c;然后遍历这个字符串把里面的字符存入字符数组&amp;#xff0c;再进行大小写转换&amp;#xff0c;最后打印结果即可&amp;#xff0c;修改如下&amp;#xff1a;&lt;br /&gt;&lt;br /&gt;参考链接&amp;#xff1a;&lt;br /&gt;&lt;a href="https://www.jb51.net/article/218760.htm" target="_blank" rel="noopener noreferrer"&gt;https://www.jb51.net/article/218760.htm&lt;/a&gt;&lt;br /&gt;&lt;a target="_blank"&gt;http://ascii.wjccx.com/&lt;/a&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-java"&gt;&lt;span class="hljs-keyword"&gt;import&lt;/span&gt; java.util.Scanner;

&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; Main {

    &lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;static&lt;/span&gt; &lt;span class="hljs-keyword"&gt;void&lt;/span&gt; main(String[] args) {
        &lt;span class="hljs-comment"&gt;// TODO Auto-generated method stub&lt;/span&gt;

        &lt;span class="hljs-keyword"&gt;char&lt;/span&gt; a;
        System.out.&lt;span class="hljs-keyword"&gt;println&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;请输入十个数组元素&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-keyword"&gt;char&lt;/span&gt; arr[] &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-keyword"&gt;char&lt;/span&gt;[&lt;span class="hljs-number"&gt;10&lt;/span&gt;];
        Scanner t &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; Scanner(System.in);

        &lt;span class="hljs-comment"&gt;//https://www.jb51.net/article/218760.htm&lt;/span&gt;
        String str &amp;#61; t.nextLine();     &lt;span class="hljs-comment"&gt;//获取一行输入存入一个字符串变量str中&lt;/span&gt;

        &lt;span class="hljs-comment"&gt;//判断这个字符串是否是10个字符&amp;#xff0c;如果不是则重新从输入获取一行字符串&amp;#xff0c;直到字符串变量正确获取到10个字符&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(str.length()!&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;) {
            System.out.&lt;span class="hljs-keyword"&gt;println&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n输入的字符个数不正确&amp;#xff0c;请输入10个字符&amp;#xff01;&amp;#34;&lt;/span&gt;);
            str &amp;#61; t.nextLine();
        }

        &lt;span class="hljs-comment"&gt;//打印原字符串中的10个字符&lt;/span&gt;
        System.out.&lt;span class="hljs-keyword"&gt;println&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n输入的10个字符为&amp;#xff1a;&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;arr.length;i&amp;#43;&amp;#43;) {
            arr[i] &amp;#61; str.charAt(i);    
            System.out.&lt;span class="hljs-keyword"&gt;print&lt;/span&gt;(arr[i]);
        }
        System.out.&lt;span class="hljs-keyword"&gt;println&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);

        &lt;span class="hljs-comment"&gt;//将大写字母转为小写字母&amp;#xff0c;小写字母转为大写字母        &lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;arr.length;i&amp;#43;&amp;#43;) {
            &lt;span class="hljs-comment"&gt;//http://ascii.wjccx.com/&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(arr[i]&amp;gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;a&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;arr[i]&amp;lt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;z&amp;#39;&lt;/span&gt;) {
                arr[i] &amp;#61; (&lt;span class="hljs-keyword"&gt;char&lt;/span&gt;)(arr[i]-&lt;span class="hljs-number"&gt;32&lt;/span&gt;);
            }&lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(arr[i]&amp;gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;A&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;arr[i]&amp;lt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;Z&amp;#39;&lt;/span&gt;) {
                arr[i] &amp;#61; (&lt;span class="hljs-keyword"&gt;char&lt;/span&gt;)(arr[i]&amp;#43;&lt;span class="hljs-number"&gt;32&lt;/span&gt;);
            }

        }

        &lt;span class="hljs-comment"&gt;//打印转换后的10个字符&lt;/span&gt;
        System.out.&lt;span class="hljs-keyword"&gt;println&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;转换后的10个字符为&amp;#xff1a;&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;arr.length;i&amp;#43;&amp;#43;) {
            System.out.&lt;span class="hljs-keyword"&gt;print&lt;/span&gt;(arr[i]);    
        }

    }

}
