    &lt;span class="hljs-type"&gt;double&lt;/span&gt; large, tiny;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%lf %lf&amp;#34;&lt;/span&gt;, &amp;amp;large, &amp;amp;tiny);
    &lt;span class="hljs-type"&gt;double&lt;/span&gt; result &amp;#61; (large * large - tiny * tiny) * &lt;span class="hljs-number"&gt;3.14159265358&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%.6lf&amp;#34;&lt;/span&gt;, result);
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;有帮助望采纳&lt;/p&gt;

&lt;pre&gt;
&lt;code&gt;#include &amp;lt;stdio.h&amp;gt;
#include &amp;lt;stdlib.h&amp;gt;

/**
 * 编写函数实现对数组中10个整数由小到大排序&amp;#xff0c;要求编写三个分别实现整数的
 * 输入&amp;#xff0c;排序&amp;#xff0c;输出&amp;#xff0c;主函只须调提示&amp;#xff1a;以数组名作为函数参数&amp;#xff0c;编写三个函数。
 * input(int a[],int n)
 * sort(int a[],int n)
 * output(int a[],int n)
 */

 /* 输入函数 */
 int input(int a[],int n)
 {
    int i &amp;#61; 0;

    printf(&amp;#34;请以此输入数组元素:\n&amp;#34;);
    for(i&amp;#61;0; i&amp;lt;n; i&amp;#43;&amp;#43;)
    {
        scanf(&amp;#34;%d&amp;#34;, &amp;amp;a[i]);
    }

    return 0;
 }

 /* 排序函数 */
 int sort(int a[],int n)
 {
     int i &amp;#61; 0, j &amp;#61; 0, tem &amp;#61; 0;

     printf(&amp;#34;正在对数组进行由小到大排序:\n&amp;#34;);
     for(i&amp;#61;0; i&amp;lt;n; i&amp;#43;&amp;#43;)
     {
         for(j&amp;#61;i&amp;#43;1; j&amp;lt;n; j&amp;#43;&amp;#43;)
         {
             if(a[j] &amp;lt; a[i])
             {
                 tem &amp;#61; a[i];
                 a[i] &amp;#61; a[j];
                 a[j] &amp;#61; tem;
             }
         }
     }
     printf(&amp;#34;排序完成&amp;#xff01;\n&amp;#34;);
     return 0;
 }

 /* 输出函数 */
 int output(int a[],int n)
 {
     int i &amp;#61; 0;
     printf(&amp;#34;输出数组: \n&amp;#34;);
     for(i&amp;#61;0; i&amp;lt;n; i&amp;#43;&amp;#43;)
     {
         printf(&amp;#34;%d &amp;#34;,a[i]);
     }
     printf(&amp;#34;\n&amp;#34;);

     return 0;
 }

int main()
{
    int *a &amp;#61; NULL;
    int n &amp;#61; 0;

    /* 输入 */
    printf(&amp;#34;请输入数组的大小:\n&amp;#34;);
    scanf(&amp;#34;%d&amp;#34;, &amp;amp;n);
    a &amp;#61; malloc(n*sizeof(n));
    input(a, n);

    /* 排序 */
    sort(a, n);

    /* 输出 */
    output(a, n);

    return 0;
}&lt;/code&gt;&lt;/pre&gt;

