&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;vector&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;functional&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;queue&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;

&lt;span class="hljs-keyword"&gt;typedef&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; ll;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n, m, s, t;
    cin &amp;gt;&amp;gt; n &amp;gt;&amp;gt; m &amp;gt;&amp;gt; s &amp;gt;&amp;gt; t;
    vector&amp;lt;vector&amp;lt;&lt;span class="hljs-type"&gt;int&lt;/span&gt;&amp;gt; &amp;gt; &lt;span class="hljs-built_in"&gt;g&lt;/span&gt;(n &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;);
    &lt;span class="hljs-function"&gt;vector&amp;lt;ll&amp;gt; &lt;span class="hljs-title"&gt;dp&lt;/span&gt;&lt;span class="hljs-params"&gt;(n &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;m;i&amp;#43;&amp;#43;){
        &lt;span class="hljs-type"&gt;int&lt;/span&gt; u, v;
        cin &amp;gt;&amp;gt; u &amp;gt;&amp;gt; v;
        g[u].&lt;span class="hljs-built_in"&gt;push_back&lt;/span&gt;(v);
    }
    &lt;span class="hljs-function"&gt;vector&amp;lt;&lt;span class="hljs-type"&gt;bool&lt;/span&gt;&amp;gt; &lt;span class="hljs-title"&gt;used&lt;/span&gt;&lt;span class="hljs-params"&gt;(n &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;;
    function&amp;lt;ll(&lt;span class="hljs-type"&gt;int&lt;/span&gt;)&amp;gt; Dfs &amp;#61; [&amp;amp;](&lt;span class="hljs-type"&gt;int&lt;/span&gt; u){
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(used[u]){
            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; dp[u];
        }
        used[u] &amp;#61; &lt;span class="hljs-literal"&gt;true&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;g[u].&lt;span class="hljs-built_in"&gt;size&lt;/span&gt;();i&amp;#43;&amp;#43;){
            &lt;span class="hljs-type"&gt;int&lt;/span&gt; v &amp;#61; g[u][i];
            dp[u] &amp;#43;&amp;#61; &lt;span class="hljs-built_in"&gt;Dfs&lt;/span&gt;(v);
        }
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; dp[u];
    };
    used[t] &amp;#61; &lt;span class="hljs-literal"&gt;true&lt;/span&gt;;
    dp[t] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-built_in"&gt;Dfs&lt;/span&gt;(s);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/910930309636173.png" class="md_img" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;
&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/55318030963616.png" class="md_img" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;

#include &amp;lt;iostream&amp;gt;

using namespace std;


int main()
{
    int a[&lt;span class="hljs-number"&gt;120&lt;/span&gt;];
    int b[&lt;span class="hljs-number"&gt;120&lt;/span&gt;];
    int* opt&amp;#61;(int*)malloc(sizeof(int)*&lt;span class="hljs-number"&gt;300001&lt;/span&gt;);
    opt[&lt;span class="hljs-number"&gt;0&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    a[&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    b[&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    int c;
    for (int i &amp;#61; &lt;span class="hljs-number"&gt;2&lt;/span&gt;; i &amp;lt;&amp;#61; &lt;span class="hljs-number"&gt;120&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        b[i] &amp;#61; (i) * (i &amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;) / &lt;span class="hljs-number"&gt;2&lt;/span&gt;;
        a[i] &amp;#61; a[i - &lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#43; b[i];
    }

    for (int i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt;&lt;span class="hljs-number"&gt;300001&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        opt[i] &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        for (int j &amp;#61; &lt;span class="hljs-number"&gt;120&lt;/span&gt;; j &amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; j--)
        {
            if (a[j]&amp;gt;i)
            {
                continue;
            }
            else
            {
                if (opt[i]&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
                {
                    opt[i] &amp;#61; opt[i - a[j]] &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                }
                else if (opt[i-a[j]]&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;&amp;lt;opt[i])
                {
                    opt[i] &amp;#61; opt[i - a[j]] &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                }
            }
        }
    }


    int n;
    cin &amp;gt;&amp;gt; n;
    int* v &amp;#61; new int[n];
    for (int i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
    {
        cin &amp;gt;&amp;gt; v[i];
    }
    for (int i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
    {
        cout &amp;lt;&amp;lt; opt[v[i]] &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;;
    }
    cin &amp;gt;&amp;gt; n;

}


&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-python"&gt;&lt;span class="hljs-string"&gt;&amp;#39;&amp;#39;&amp;#39;tools.py&amp;#39;&amp;#39;&amp;#39;&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;Animal&lt;/span&gt;:

    &lt;span class="hljs-built_in"&gt;type&lt;/span&gt; &amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;狗&amp;#34;&lt;/span&gt; &lt;span class="hljs-comment"&gt;#类属性&lt;/span&gt;

    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;__init__&lt;/span&gt;(&lt;span class="hljs-params"&gt;self, name&lt;/span&gt;):
        self.name &amp;#61; name &lt;span class="hljs-comment"&gt;#实例属性&lt;/span&gt;
        self.__age &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &lt;span class="hljs-comment"&gt;#私有属性&amp;#xff0c;不能在类外面访问&lt;/span&gt;

    &lt;span class="hljs-keyword"&gt;def&lt;/span&gt; &lt;span class="hljs-title function_"&gt;eat&lt;/span&gt;(&lt;span class="hljs-params"&gt;self&lt;/span&gt;):
        &lt;span class="hljs-built_in"&gt;print&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;狗吃骨头&amp;#34;&lt;/span&gt;)


&lt;span class="hljs-string"&gt;&amp;#39;&amp;#39;&amp;#39;demo.py&amp;#39;&amp;#39;&amp;#39;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;from&lt;/span&gt; tools &lt;span class="hljs-keyword"&gt;import&lt;/span&gt; *

&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;Dog&lt;/span&gt;(&lt;span class="hljs-title class_ inherited__"&gt;Animal&lt;/span&gt;):
