public abstract class Person {
	public abstract void toScreen();
}

public class Teacher extends Person{
	public Teacher(String data){
		intData=new int[4];
		String[]stringData=data.split(" ");
		for(int i=0;i&lt;4;i++){
			intData[i]=Integer.parseInt(stringData[i]);
		}
	}
	public void toScreen(){
		System.out.println("teacher "+(intData[0]+intData[1]+intData[2]+intData[3])/4);
	}
	private int[]intData;
}

public class Student extends Person{
	public Student(String data){
		intData=new int[3];
		String[]stringData=data.split(" ");
		for(int i=0;i&lt;3;i++){
			intData[i]=Integer.parseInt(stringData[i]);
		}
	}
	public void toScreen(){
		System.out.println("student "+(intData[0]+intData[1]+intData[2])/3);
	}
	private int[]intData;
}

