int main(void)
{
    int M, N;
    int i, j;
    int k, count = 0;
    char **a;
    int *x, *y;
    // 行列 
    scanf("%d %d", &M, &N);
    if(M>0&&M<20 && N>0&&N<20)
    {
        // 动态分配内存 
        // 行指针 
        a = (char **)malloc(sizeof(char *)*M);
        for(i = 0;i < M;i++)
        {
            a[i] = (char *)malloc(sizeof(char)*N);
        }
        // 得到蚊子分布图 
        for(i = 0;i < M;i++)
        {
            getchar();
            for(j = 0;j < N;j++)
            {
                a[i][j] = getchar();
            }
        }
        // 炮弹数量 
        scanf("%d", &k);
        // 动态分配一维数组存储炮弹命中坐标 
        x = (int *)malloc(sizeof(int)*k);
        y = (int *)malloc(sizeof(int)*k);
        if(k>0 && k<400)
        {
            for(i = 0;i < k;i++)
            {
                scanf("%d %d", &x[i], &y[i]);
            }
            for(i = 0;i < k;i++)
            {
                if(x[i]>=0&&x[i]<M&&y[i]>=0&&y[i]<N)
                {
                    // 第一发炮弹 
                    if(i == 0)
                    {
                        // 直接命中，蚊子消失，生命为0 
                        if(a[x[i]][y[i]] == '#')
                        {
                            a[x[i]][y[i]] = '0';
                            count++;
                        }

                        // 炮弹波及上下左右的蚊子，标记蚊子生命为1
                        if(a[x[i]][y[i]+1] == '#') 
                        {
                            a[x[i]][y[i]+1] = '1';
                        }
                        if(a[x[i]][y[i]-1] == '#')
                        {
                            a[x[i]][y[i]-1] = '1';
                        }
                        if(a[x[i]+1][y[i]] == '#')
                        {
                            a[x[i]+1][y[i]] = '1';
                        }
                        if(a[x[i]-1][y[i]] == '#')
                        {
                            a[x[i]-1][y[i]] = '1';
                        }
                    }
                    else
                    {
                        // 直接命中，或命中生命为1的蚊子，蚊子消失 
                        if(a[x[i]][y[i]] == '#' || a[x[i]][y[i]] == '1')
                        {
                            a[x[i]][y[i]] = '0';
                            count++;
                        }

                        //  波及生命为1的蚊子，蚊子消失
                        if(a[x[i]][y[i]+1] == '1')
                        {
                            a[x[i]][y[i]+1] = '0';
                            count++;
                        }
                        else if(a[x[i]][y[i]+1] == '#')// 波及健全的蚊子，标记蚊子生命为1 
                        {
                            a[x[i]][y[i]+1] = '1';
                        }

                        if(a[x[i]][y[i]-1] == '1')
                        {
                            a[x[i]][y[i]-1] = '0';
                            count++;
                        }
                        else if(a[x[i]][y[i]-1] == '#')
                        {
                            a[x[i]][y[i]-1] = '1';
                        }

                        if(a[x[i]+1][y[i]] == '1')
                        {
                            a[x[i]+1][y[i]] = '0';
                            count++;
                        }
                        else if(a[x[i]+1][y[i]] == '#')
                        {
                            a[x[i]+1][y[i]] = '1';
                        }

                        if(a[x[i]-1][y[i]] == '1')
                        {
                            a[x[i]-1][y[i]] = '0';
                            count++;
                        }
                        else if(a[x[i]-1][y[i]] == '#')
                        {
                            a[x[i]-1][y[i]] = '1';
                        }
                    }
                }
                printf("%d\n", count);
                count = 0;
            }
        }
    }

    // 释放内存 
    for(i = 0;i < M;i++)
    {
        free(a[i]);
    }
    free(a);
    free(x);
    free(y);
    return 0;
}

