            &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;False&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-literal"&gt;True&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; i &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; &lt;span class="hljs-built_in"&gt;range&lt;/span&gt;(&lt;span class="hljs-number"&gt;10&lt;/span&gt;,&lt;span class="hljs-number"&gt;21&lt;/span&gt;):
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; isPrime(i):
