&lt;pre&gt;&lt;code class="language-python"&gt;l&amp;#61;&lt;span class="hljs-selector-tag"&gt;input&lt;/span&gt;()&lt;span class="hljs-selector-class"&gt;.split&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#39;.&amp;#39;&lt;/span&gt;)
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt; int(l&lt;span class="hljs-selector-attr"&gt;[0]&lt;/span&gt;)&amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;:
    p&amp;#61;len(l&lt;span class="hljs-selector-attr"&gt;[0]&lt;/span&gt;)-&lt;span class="hljs-number"&gt;1&lt;/span&gt;
    print(l&lt;span class="hljs-selector-attr"&gt;[0]&lt;/span&gt;&lt;span class="hljs-selector-attr"&gt;[0]&lt;/span&gt;&amp;#43;&lt;span class="hljs-string"&gt;&amp;#39;.&amp;#39;&lt;/span&gt;&amp;#43;l&lt;span class="hljs-selector-attr"&gt;[0]&lt;/span&gt;&lt;span class="hljs-selector-attr"&gt;[1:]&lt;/span&gt;&amp;#43;l&lt;span class="hljs-selector-attr"&gt;[1]&lt;/span&gt;&amp;#43;&lt;span class="hljs-string"&gt;&amp;#39;e&amp;#39;&lt;/span&gt;&amp;#43;str(p))
&lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
    p&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-selector-tag"&gt;i&lt;/span&gt; &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; l&lt;span class="hljs-selector-attr"&gt;[1]&lt;/span&gt;:
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-selector-tag"&gt;i&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;0&amp;#39;&lt;/span&gt;:
            p&amp;#43;&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
            break
    t&amp;#61;l&lt;span class="hljs-selector-attr"&gt;[1]&lt;/span&gt;&lt;span class="hljs-selector-attr"&gt;[p-1:]&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; len(t) &amp;gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;:
        t&amp;#61;t&lt;span class="hljs-selector-attr"&gt;[0]&lt;/span&gt;&amp;#43;&lt;span class="hljs-string"&gt;&amp;#39;.&amp;#39;&lt;/span&gt;&amp;#43;t&lt;span class="hljs-selector-attr"&gt;[1:]&lt;/span&gt;
    print(t&amp;#43;&lt;span class="hljs-string"&gt;&amp;#39;e-&amp;#39;&lt;/span&gt;&amp;#43;str(p))
