&lt;p&gt;如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;compare&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;float&lt;/span&gt;* a, &lt;span class="hljs-type"&gt;float&lt;/span&gt;* b)&lt;/span&gt;&lt;/span&gt;;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;compare&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;float&lt;/span&gt;* a, &lt;span class="hljs-type"&gt;float&lt;/span&gt;* b)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;float&lt;/span&gt; t;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (*a &amp;lt;&amp;#61; *b)
    {
        t &amp;#61; *a;
        *a &amp;#61; *b;
        *b &amp;#61; t;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    }
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;float&lt;/span&gt; x, y;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; ret;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%f %f&amp;#34;&lt;/span&gt;, &amp;amp;x, &amp;amp;y);
    ret &amp;#61; &lt;span class="hljs-built_in"&gt;compare&lt;/span&gt;(&amp;amp;x, &amp;amp;y);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d, %f, %f&amp;#34;&lt;/span&gt;, ret, x, y);
