&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;vector&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;string.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;



&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{

    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; val;  &lt;span class="hljs-comment"&gt;//用于缴纳&lt;/span&gt;
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; name[&lt;span class="hljs-number"&gt;10&lt;/span&gt;];
    &lt;span class="hljs-comment"&gt;//一共有5人&lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; banf[&lt;span class="hljs-number"&gt;5&lt;/span&gt;];
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-number"&gt;5&lt;/span&gt;;&amp;#43;&amp;#43;i)
    {
        banf[i]&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;; &lt;span class="hljs-comment"&gt;//初始每人缴纳10元班费&lt;/span&gt;
    }
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; jiaofeimingdan[&lt;span class="hljs-number"&gt;5&lt;/span&gt;][&lt;span class="hljs-number"&gt;10&lt;/span&gt;];
    &lt;span class="hljs-built_in"&gt;sprintf&lt;/span&gt;(jiaofeimingdan[&lt;span class="hljs-number"&gt;0&lt;/span&gt;],&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#34;张三&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;sprintf&lt;/span&gt;(jiaofeimingdan[&lt;span class="hljs-number"&gt;1&lt;/span&gt;],&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#34;李四&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;sprintf&lt;/span&gt;(jiaofeimingdan[&lt;span class="hljs-number"&gt;2&lt;/span&gt;],&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#34;王五&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;sprintf&lt;/span&gt;(jiaofeimingdan[&lt;span class="hljs-number"&gt;3&lt;/span&gt;],&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#34;甲&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;sprintf&lt;/span&gt;(jiaofeimingdan[&lt;span class="hljs-number"&gt;4&lt;/span&gt;],&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,&lt;span class="hljs-string"&gt;&amp;#34;乙&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(&lt;span class="hljs-literal"&gt;true&lt;/span&gt;)
    {
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;***0:个人缴费查询 ***\n&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;***1:班 费 缴 纳 ***\n&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;***2:退 出 系 统 ***\n&amp;#34;&lt;/span&gt;);
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;n);
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(n&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        {
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;请输入要查询的姓名:\n&amp;#34;&lt;/span&gt;);
            &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,&amp;amp;name);
            &lt;span class="hljs-type"&gt;bool&lt;/span&gt; isFind&amp;#61;&lt;span class="hljs-literal"&gt;false&lt;/span&gt;;
