            &lt;span class="hljs-built_in"&gt;swap&lt;/span&gt;(&amp;amp;a[i],&amp;amp;a[i&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]);
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;s;i&amp;gt;e;i--)
            &lt;span class="hljs-built_in"&gt;swap&lt;/span&gt;(&amp;amp;a[i],&amp;amp;a[i&lt;span class="hljs-number"&gt;-1&lt;/span&gt;]);
 }

&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;string.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; a[N][N];
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Please enter a 5x5 matrix: \n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; N; i&amp;#43;&amp;#43;) {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; N; j&amp;#43;&amp;#43;) {
            &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;a[i][j]);
        }
    }
    &lt;span class="hljs-built_in"&gt;sort&lt;/span&gt;(a, N);
    &lt;span class="hljs-built_in"&gt;mone&lt;/span&gt;(a,&lt;span class="hljs-number"&gt;24&lt;/span&gt;,&lt;span class="hljs-number"&gt;14&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;mone&lt;/span&gt;(a,&lt;span class="hljs-number"&gt;3&lt;/span&gt;,&lt;span class="hljs-number"&gt;24&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;mone&lt;/span&gt;(a,&lt;span class="hljs-number"&gt;2&lt;/span&gt;,&lt;span class="hljs-number"&gt;20&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;mone&lt;/span&gt;(a,&lt;span class="hljs-number"&gt;1&lt;/span&gt;,&lt;span class="hljs-number"&gt;4&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; N; i&amp;#43;&amp;#43;) {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; N; j&amp;#43;&amp;#43;) {
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;, a[i][j]);
