    &lt;span class="hljs-built_in"&gt;system&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;pause&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

