&lt;h1&gt;简单插入排序&lt;/h1&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;#34;stdio.h&amp;#34;&lt;/span&gt;
void display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;int&lt;/span&gt;&lt;span class="hljs-operator"&gt;*&lt;/span&gt;&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;n&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;){
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
    }
    printf(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
}
&lt;span class="hljs-comment"&gt;// 直接插入排序:默认排序结果是非递减有序序列&lt;/span&gt;
void direct&lt;span class="hljs-constructor"&gt;InsertSort(&lt;span class="hljs-params"&gt;int&lt;/span&gt;&lt;span class="hljs-operator"&gt;*&lt;/span&gt;&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;length&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; tmp,i, j;
    display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;length&lt;/span&gt;)&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;i&amp;lt;length;i&amp;#43;&amp;#43;){
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;&amp;lt;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;-&lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt;){
            tmp&amp;#61;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;i-&lt;span class="hljs-number"&gt;1&lt;/span&gt;;j&amp;gt;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;&amp;amp;&amp;amp;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&amp;gt;tmp;j--){
                a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt;&amp;#61;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;;
            }
            a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt;&amp;#61;tmp;
        }
        display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;length&lt;/span&gt;)&lt;/span&gt;;
    }
}
&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; main&lt;span class="hljs-literal"&gt;()&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; a&lt;span class="hljs-literal"&gt;[]&lt;/span&gt;&amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;43&lt;/span&gt;,&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;79&lt;/span&gt;,&lt;span class="hljs-number"&gt;22&lt;/span&gt;};
    direct&lt;span class="hljs-constructor"&gt;InsertSort(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;sizeof&lt;/span&gt;(&lt;span class="hljs-params"&gt;a&lt;/span&gt;)&lt;/span&gt;/sizeof(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;));
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i,len&amp;#61;sizeof(a)/sizeof(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;);
    printf(&lt;span class="hljs-string"&gt;&amp;#34;Result:\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len;i&amp;#43;&amp;#43;){
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
    }
}

&lt;/code&gt;&lt;/pre&gt;
