&lt;h1&gt;选择排序&lt;/h1&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;#34;stdio.h&amp;#34;&lt;/span&gt;
void display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;int&lt;/span&gt;&lt;span class="hljs-operator"&gt;*&lt;/span&gt;&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;n&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;){
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
    }
    printf(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
}
void swap(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;*a,&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;*b){(*a)^&amp;#61;(*b);(*b)^&amp;#61;(*a);(*a)^&amp;#61;(*b);}
void select&lt;span class="hljs-constructor"&gt;Sort(&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;a&lt;/span&gt;[],&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; minpos&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;,i,j;
    display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len-&lt;span class="hljs-number"&gt;1&lt;/span&gt;;i&amp;#43;&amp;#43;){
        minpos&amp;#61;i;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;i&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;j&amp;lt;len;j&amp;#43;&amp;#43;){
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&amp;lt;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;minpos&lt;/span&gt;]&lt;/span&gt;) minpos&amp;#61;j;
        }
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(minpos!&amp;#61;i){
            swap(&amp;amp;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;,&amp;amp;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;minpos&lt;/span&gt;]&lt;/span&gt;);
        }
        display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;;
    }
}
&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; main&lt;span class="hljs-literal"&gt;()&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; a&lt;span class="hljs-literal"&gt;[]&lt;/span&gt;&amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;43&lt;/span&gt;,&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;79&lt;/span&gt;,&lt;span class="hljs-number"&gt;22&lt;/span&gt;};
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; len &amp;#61; sizeof(a)/sizeof(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;);
    select&lt;span class="hljs-constructor"&gt;Sort(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i;
    printf(&lt;span class="hljs-string"&gt;&amp;#34;Result:\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len;i&amp;#43;&amp;#43;){
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
    }
}

&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img width="600px" align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/766161675636133.png" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;
&lt;h1&gt;冒泡排序&lt;/h1&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;#34;stdio.h&amp;#34;&lt;/span&gt;
void display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;int&lt;/span&gt;&lt;span class="hljs-operator"&gt;*&lt;/span&gt;&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;n&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;){
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
    }
    printf(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
}
void swap(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;*a,&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;*b){ (*a)^&amp;#61;(*b);(*b)^&amp;#61;(*a);(*a)^&amp;#61;(*b);}
&lt;span class="hljs-comment"&gt;//线性排序的冒泡排序:递增序列&lt;/span&gt;
void bubble&lt;span class="hljs-constructor"&gt;Sort(&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;a&lt;/span&gt;[],&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; flag;
    display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i&amp;#61;len-&lt;span class="hljs-number"&gt;1&lt;/span&gt;;i&amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i--) {
        flag&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;i;j&amp;#43;&amp;#43;){
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&amp;gt;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt;){&lt;!-- --&gt;&lt;span class="hljs-comment"&gt;//将大的后移&lt;/span&gt;
                flag&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                swap(&amp;amp;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;,&amp;amp;a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt;);
            }
        }
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(!flag) break;
        display&lt;span class="hljs-constructor"&gt;Array(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;;
    }  
}
&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; main&lt;span class="hljs-literal"&gt;()&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; a&lt;span class="hljs-literal"&gt;[]&lt;/span&gt;&amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;43&lt;/span&gt;,&lt;span class="hljs-number"&gt;4&lt;/span&gt;,&lt;span class="hljs-number"&gt;79&lt;/span&gt;,&lt;span class="hljs-number"&gt;22&lt;/span&gt;};
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; len &amp;#61; sizeof(a)/sizeof(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt;);
    bubble&lt;span class="hljs-constructor"&gt;Sort(&lt;span class="hljs-params"&gt;a&lt;/span&gt;,&lt;span class="hljs-params"&gt;len&lt;/span&gt;)&lt;/span&gt;;
    printf(&lt;span class="hljs-string"&gt;&amp;#34;Result:\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len;i&amp;#43;&amp;#43;){
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;);
    }
}

&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img width="600px" align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/380302675636172.png" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;
&lt;h1&gt;背包问题&lt;/h1&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-comment"&gt;//一维数组解法&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; MAX_M 12880        &lt;span class="hljs-comment"&gt;//最大限重&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; MAX_N 3402        &lt;span class="hljs-comment"&gt;// 最大种类数&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; max(a,b) a&amp;gt;b?a:b&lt;/span&gt;

&lt;span class="hljs-type"&gt;int&lt;/span&gt; dp[MAX_M &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;]&amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;};
&lt;span class="hljs-type"&gt;int&lt;/span&gt; weights[MAX_N &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;];
&lt;span class="hljs-type"&gt;int&lt;/span&gt; values[MAX_N &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;];

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt; &lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n, m, i, j;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d%d&amp;#34;&lt;/span&gt;, &amp;amp;n , &amp;amp;m);&lt;span class="hljs-comment"&gt;//n是种类&amp;#xff0c;m是限制重量&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt;&amp;#61; n; i&amp;#43;&amp;#43;) {
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d%d&amp;#34;&lt;/span&gt;, &amp;amp;weights[i] ,&amp;amp;values[i]);
    }
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt;&amp;#61; n; i&amp;#43;&amp;#43;) {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (j &amp;#61; m; j &amp;gt;&amp;#61; weights[i]; j--) {
            dp[j] &amp;#61; &lt;span class="hljs-built_in"&gt;max&lt;/span&gt;(dp[j], dp[j - weights[i]] &amp;#43; values[i]);
        }
    }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d\n&amp;#34;&lt;/span&gt;, dp[m] );
}
