&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt; &lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; str1[&lt;span class="hljs-number"&gt;50&lt;/span&gt;],str2[&lt;span class="hljs-number"&gt;50&lt;/span&gt;];
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; res&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;字符串1\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;gets&lt;/span&gt;(str1);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;字符串2\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;gets&lt;/span&gt;(str2);
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; *p1&amp;#61;str1,*p2&amp;#61;str2;&lt;span class="hljs-comment"&gt;//p1 p2 指向str1 和str2两个字符串的第一个字符&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (*p1 &amp;#61;&amp;#61; *p2 &amp;amp;&amp;amp; *p1){
        p1&amp;#43;&amp;#43;; p2&amp;#43;&amp;#43;;
    }
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (*p1 &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt; &amp;amp;&amp;amp; *p2 &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;)
        res &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        res &amp;#61; *p1 - *p2;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,res);

&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;if&lt;/span&gt; 0     &lt;span class="hljs-comment"&gt;//以下删除&lt;/span&gt;&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(*p1!&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;||*p2!&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(*p1&amp;gt;*p2) &lt;span class="hljs-comment"&gt;//如果p1指向当前字符大于p2所指向的字符 若p1指向b p2指向a此时 b大于a&lt;/span&gt;
            {
                res&amp;#61;*p1-*p2;&lt;span class="hljs-comment"&gt;//结果等于b-a&amp;#61;1&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;; &lt;span class="hljs-comment"&gt;//跳出循环&lt;/span&gt;
            }
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (*p2&amp;lt;*p1)
            {
                res&amp;#61;*p1-*p2;
                &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
            }
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;&lt;span class="hljs-comment"&gt;//都不满足&lt;/span&gt;
        {
        p1&amp;#43;&amp;#43;;
        p2&amp;#43;&amp;#43;;     &lt;span class="hljs-comment"&gt;//判断下一字符&lt;/span&gt;
        }
    }
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(*p1&amp;#61;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;*p2&amp;#61;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;)&lt;span class="hljs-comment"&gt;//若p1 和p2 都指向&amp;#39;\0&amp;#39;说明俩字符串等长且相等&lt;/span&gt;
        {
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;两字符串相等&amp;#34;&lt;/span&gt;);
            res&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        } 
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(*p1&amp;#61;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;*p2!&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;)&lt;span class="hljs-comment"&gt;//若p1指向‘\0’而p2指向不是‘\0’ 说明str2长度大于str1且在p1指向/0的位置前&lt;/span&gt;
        {                                                                &lt;span class="hljs-comment"&gt;//两字符完全相等&lt;/span&gt;
            res&amp;#61;-*p2;&lt;span class="hljs-comment"&gt;//res&amp;#61;0-p2指向的字符 &lt;/span&gt;
        }
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(*p1!&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;*p2&amp;#61;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;)
        res &amp;#61;*p1;
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n\n%d&amp;#34;&lt;/span&gt;,res);
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;endif&lt;/span&gt;&lt;/span&gt;

    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

