&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;    &lt;span class="hljs-keyword"&gt;import&lt;/span&gt; java.util.Scanner;

    &lt;span class="hljs-built_in"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; Main {
        &lt;span class="hljs-built_in"&gt;public&lt;/span&gt; static &lt;span class="hljs-type"&gt;void&lt;/span&gt; main(String[] args) {

            Scanner &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; &amp;#61; &lt;span class="hljs-built_in"&gt;new&lt;/span&gt; Scanner(&lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;in&lt;/span&gt;);

            &lt;span class="hljs-type"&gt;int&lt;/span&gt; num &amp;#61; &lt;span class="hljs-keyword"&gt;in&lt;/span&gt;.nextInt();

            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (num&amp;gt;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;) {

                myPrint(num);

            }&lt;span class="hljs-keyword"&gt;else&lt;/span&gt; {

                num &amp;#61; Math.abs(num);
                myPrint(num);

            }

            &lt;span class="hljs-keyword"&gt;in&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;close&lt;/span&gt;();
        }

        &lt;span class="hljs-built_in"&gt;public&lt;/span&gt; static &lt;span class="hljs-type"&gt;void&lt;/span&gt; myPrint(&lt;span class="hljs-type"&gt;int&lt;/span&gt; num) {
             // 取输入数值中的每一位
            &lt;span class="hljs-type"&gt;int&lt;/span&gt;[] &lt;span class="hljs-keyword"&gt;all&lt;/span&gt; &amp;#61; {
                    (num/&lt;span class="hljs-number"&gt;10000&lt;/span&gt;)%&lt;span class="hljs-number"&gt;10&lt;/span&gt;,
                    (num/&lt;span class="hljs-number"&gt;1000&lt;/span&gt;)%&lt;span class="hljs-number"&gt;10&lt;/span&gt;,
                    (num/&lt;span class="hljs-number"&gt;100&lt;/span&gt;)%&lt;span class="hljs-number"&gt;10&lt;/span&gt;,
                    (num/&lt;span class="hljs-number"&gt;10&lt;/span&gt;)%&lt;span class="hljs-number"&gt;10&lt;/span&gt;,
                    num%&lt;span class="hljs-number"&gt;10&lt;/span&gt;
            };

            // 找到第一个数值不为&lt;span class="hljs-number"&gt;0&lt;/span&gt;的元素的下标
            &lt;span class="hljs-type"&gt;int&lt;/span&gt; head &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;.length;i&amp;#43;&amp;#43;) {
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;[head]&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
                    head&amp;#43;&amp;#43;;
                }
            }
            // 计算并输出   输入值的位数
            &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println(&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;.length-head);

            // 分隔输出每位
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j&amp;#61;head;j&amp;lt;&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;.length&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;j&amp;#43;&amp;#43;) {
                &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.print(&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;[j]&amp;#43;&amp;#34; &amp;#34;);
            }
            &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.println(&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;[&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;.length&lt;span class="hljs-number"&gt;-1&lt;/span&gt;]);

            // 倒序输出每位
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j&amp;#61;&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;.length&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;j&amp;gt;&amp;#61;head;j&lt;span class="hljs-comment"&gt;--) {&lt;!-- --&gt;&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.&lt;span class="hljs-keyword"&gt;out&lt;/span&gt;.print(&lt;span class="hljs-keyword"&gt;all&lt;/span&gt;[j]);
            }
        }
    }
&lt;/code&gt;&lt;/pre&gt;

