#include<stdlib.h>
#define N 100000

int cmp(char *s, char *t){return *s - *t;} // 比较函数
// 去掉t中的空白字符，升序排序后存入s
static inline void trip_sort(char *s, char *t){
    char *p, *delim = "\x20\t"; // 空白字符
    p = strtok(t, delim);
    *s = 0;
    do{ strcat(s, p); }while((p = strtok(NULL, delim)));
    qsort(s, strlen(s), 1, (void*)cmp);
}
