double funcos( double e, double x ){
double m=0;
int flag=1;
double temp=0;
double g=1;
int f=0;
int i;
do {
for ( i=1;i&lt;=f;i++){
g*=i;
}
m=pow(x,f)/g;
f+=2;
temp+=m*flag;
flag=-flag;
g=1;
}while(m&gt;e);
return temp;
}

