        &lt;span class="hljs-keyword"&gt;try&lt;/span&gt; {
            bw1 &amp;#61; &lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;BufferedWriter&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;new&lt;/span&gt; &lt;span class="hljs-title class_"&gt;FileWriter&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;d:\\c.txt&amp;#34;&lt;/span&gt;));
