&lt;p&gt;那个随机数是输入的&amp;#xff0c;不需要随机产生&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;int main()
{
    int &lt;span class="hljs-built_in"&gt;number&lt;/span&gt;, N;
    scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d%d&amp;#34;&lt;/span&gt;, &amp;amp;&lt;span class="hljs-built_in"&gt;number&lt;/span&gt;, &amp;amp;N);
    int &lt;span class="hljs-built_in"&gt;count&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    int &lt;span class="hljs-built_in"&gt;item&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;count&lt;/span&gt; &amp;lt; N)
    {
        scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;&lt;span class="hljs-built_in"&gt;item&lt;/span&gt;);
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;item&lt;/span&gt; &amp;lt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
            break;

        &lt;span class="hljs-built_in"&gt;count&lt;/span&gt;&amp;#43;&amp;#43;;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;item&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-built_in"&gt;number&lt;/span&gt;)
        {
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;count&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;)
            {
