&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,b;
&lt;span class="hljs-type"&gt;char&lt;/span&gt; c1,c2;
&lt;span class="hljs-type"&gt;float&lt;/span&gt; x,y;
&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;a&amp;#61;%d,b&amp;#61;%d&amp;#34;&lt;/span&gt;,&amp;amp;a,&amp;amp;b);
&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%f,%f&amp;#34;&lt;/span&gt;,&amp;amp;x,&amp;amp;y);
&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%c,%c&amp;#34;&lt;/span&gt;,&amp;amp;c1,&amp;amp;c2);
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-comment"&gt;#include &amp;lt;stdio.h&amp;gt;&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; main()
{
    char ch;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; ((ch &amp;#61; getchar()) !&amp;#61; EOF)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (ch &amp;lt; &lt;span class="hljs-number"&gt;32&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;^%c %3d\n&amp;#34;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#39;&amp;#64;&amp;#39;&lt;/span&gt; &amp;#43; ch, (&lt;span class="hljs-keyword"&gt;int&lt;/span&gt;)ch);
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (ch &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;127&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;^? %3d\n&amp;#34;&lt;/span&gt;, (&lt;span class="hljs-keyword"&gt;int&lt;/span&gt;)ch);
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%2c %3d\n&amp;#34;&lt;/span&gt;, ch, (&lt;span class="hljs-keyword"&gt;int&lt;/span&gt;)ch);
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-javascript"&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; ch;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%c&amp;#34;&lt;/span&gt;,&amp;amp;ch);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;ASCII&amp;#61;%d\n&amp;#34;&lt;/span&gt;,ch);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;10进制&amp;#61;%d\n&amp;#34;&lt;/span&gt;,ch); 
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;8进制&amp;#61;%o\n&amp;#34;&lt;/span&gt;,ch);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;16进制&amp;#61;%x\n&amp;#34;&lt;/span&gt;,ch);
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}


&lt;/code&gt;&lt;/pre&gt;
