import java.util.Scanner;
public class Calculate {
	String m_exp=new String() ,p_exp=new String();
	int result=0;
	public Calculate(String exp) {
	 	m_exp=exp;
    }
    int counter(){
    	int stk[]=new int[100],a,b;
    	int top=-1;
    	for(int i=0;i&lt;p_exp.length();i++){
    		if(p_exp.charAt(i)&gt;='0'&amp;&amp;p_exp.charAt(i)&lt;='9'){
    			stk[++top]=Character.getNumericValue(p_exp.charAt(i));
    		}
    		else{
    			switch (p_exp.charAt(i)) {
				case '+':
					a = stk[top--];
					b = stk[top--];
					stk[++top]=a+b;
					break;
				case '*':
					a = stk[top--];
					b = stk[top--];
					stk[++top]=a*b;
					break;
				case '-':
					a = stk[top--];
					b = stk[top--];
					stk[++top]=b-a;
					break;
				case '/':
					a = stk[top--];
					b = stk[top--];
					if(a==0)
						return 1;
					stk[++top]=b/a;
					break;
				}

    		}
    	}
    	
    	if(top!=0)
    		return 1;
    	result=stk[0];
    	return 0;
    }
	String trans(){
		char stk[]=new char[100];
		int top=-1;
		String operators=new String("+-*/(");

		for (int i = 0; i &lt; m_exp.length(); i++) {
			if (operators.indexOf(m_exp.charAt(i)) != -1) {      //å‡ºç°æ“ä½œç¬¦
				if (m_exp.charAt(i) == '(')         //æ ˆä¸­æ·»åŠ å·¦æ‹¬å·
					stk[++top]=m_exp.charAt(i);
				else {					    //æ“ä½œç¬¦çš„ä¼˜å…ˆçº§åˆ¤æ–­
					while ((top&gt;=0) &amp;&amp; (priority(m_exp.charAt(i)) &lt;= priority(stk[top]))) {    //å½“æ ˆä¸ä¸ºç©ºæ—¶ï¼Œè¿›è¡Œä¼˜å…ˆçº§åˆ¤æ–­
						p_exp+=stk[top--];   //è‹¥å½“å‰æ“ä½œç¬¦ä¼˜å…ˆçº§ä½äºæ ˆé¡¶ï¼Œå¼¹å‡ºæ ˆé¡¶ï¼Œæ”¾åˆ°åç¼€å¼ä¸­
					}
					stk[++top]=(m_exp.charAt(i));             //å°†å½“å‰æ“ä½œç¬¦å…¥æ ˆ
				}
			}
			else if (m_exp.charAt(i) == ')') {            //å‡ºç°å³æ‹¬å·æ—¶ï¼Œå°†æ ˆä¸­å…ƒç´ ä¸€ç›´å¼¹å‡ºï¼Œç›´è‡³å¼¹å‡ºå·¦æ‹¬å·
				while (stk[top] != '(') {
					p_exp+=stk[top--];
				}
				top--;                       //å¼¹å‡ºå·¦æ‹¬å·
			}
			else {           //æŠŠæ“ä½œæ•°åŠ å…¥åˆ°åç¼€å¼ä¸­
				p_exp+=m_exp.charAt(i);
			}
	 
		}
		while (top&gt;=0) {    //å°†æ ˆä¸­å‰©ä½™æ“ä½œç¬¦æ”¾åˆ°åç¼€å¼ä¸­
			p_exp+=stk[top--];
		}
		return p_exp;
	}
	int priority(char ch){
		if (ch == '*'||ch=='/')
			return 2;
		if (ch == '+'||ch=='-')
			return 1;
		if (ch == '(')
			return -1;
