// Q715047.cpp : Defines the entry point for the console application.
//

#include "stdafx.h"


#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;

#define TRUE 1
#define FALSE 0
#define Stack_Size 50

typedef char StackElementType; //è¿ç®—ç¬¦ 
typedef double StackElementType_num; //è¿ç®—æ•° 
typedef struct
{
    StackElementType elem[Stack_Size];
    int top;
}SeqStack; //è¿ç®—ç¬¦æ ˆ 
typedef struct
{
    StackElementType_num elem[Stack_Size];
    int top;
}SeqStack_num; //è¿ç®—æ•°æ ˆ 

StackElementType_num ExpEvaluation();

void InitStack(SeqStack *S);
void InitStack_num(SeqStack_num *S);
int Push(SeqStack *S,StackElementType x);
int Push_num(SeqStack_num *S,StackElementType_num x);

int Pop(SeqStack *S,StackElementType *x);
int Pop_num(SeqStack_num *S,StackElementType_num *x);
int GetTop(SeqStack *S,StackElementType *x);
int GetTop_num(SeqStack_num *S,StackElementType_num *x);
StackElementType Compare(StackElementType ch1,StackElementType ch2);
StackElementType_num Execute(StackElementType_num a,StackElementType op,StackElementType_num b);

int main()
{
    printf("%lf",ExpEvaluation()); 
    return(0);
}
StackElementType_num ExpEvaluation()
{
    char exp[100];//ç”¨å­—ç¬¦æ•°ç»„å°†è¿ç®—è¡¨è¾¾å¼å­˜èµ·æ¥ 
    int i=0,count=0;
    printf("Please input an expression (Ending with #:)");
    exp[i]=getchar();
    while(exp[i]!='#')
    {
        i++;count++; 
        exp[i]=getchar();
    }
    SeqStack OPTR;
    SeqStack_num OVS;
    StackElementType ch,op,x;
    StackElementType_num a,b,v;
    InitStack(&amp;OPTR);
    InitStack_num(&amp;OVS);
    Push(&amp;OPTR,'#');
    for(i=0;i&lt;count;i++)
    {
        ch=exp[i];
        while(ch!='#'||GetTop(&amp;OPTR,&amp;x)!='#')
        {
          if(ch&gt;='0'&amp;&amp;ch&lt;='9')//ä¸æ˜¯æ“ä½œç¬¦ï¼Œæ˜¯æ“ä½œæ•°ï¼Œè¿›OVSæ ˆ 
          {
            int temp;
            temp=ch-'0';//å…ˆæŠŠå½“å‰æ“ä½œæ•°ä»å­—ç¬¦å˜ä¸ºæ•°å­— 
            i++;
            ch=exp[i];
            while(ch&gt;='0'&amp;&amp;ch&lt;='9')//ç»§ç»­åˆ¤æ–­ä¸‹ä¸€ä½æ˜¯å¦ä¸ºæ“ä½œæ•°
            {
                temp=temp*10+ch-'0';
                i++;
                ch=exp[i];
            } 
            Push_num(&amp;OVS,temp);//å‹æ ˆ 

          }
          else
          switch(Compare(ch,GetTop(&amp;OPTR,&amp;x)))
          {
            case '&gt;':Push(&amp;OPTR,ch);
              i++;ch=exp[i];
            case '=':Pop(&amp;OPTR,&amp;op);i++;ch=exp[i];break;//è„±æ‹¬å· 
            case '&lt;':Pop(&amp;OPTR,&amp;op);//å½¢æˆè¿ç®— 
            Pop_num(&amp;OVS,&amp;b);
            Pop_num(&amp;OVS,&amp;a);
            v=Execute(a,op,b);
            Push_num(&amp;OVS,v);
            break; 
          }
        }
    }

    GetTop_num(&amp;OVS,&amp;v);
    return(v);
} 

//åˆå§‹åŒ–
void InitStack(SeqStack *S)
{
    /*æ„é€ ä¸€ä¸ªç©ºæ ˆS*/
    S-&gt;top=-1; 
} 
void InitStack_num(SeqStack_num *S)
{
    /*æ„é€ ä¸€ä¸ªç©ºæ ˆS*/
    S-&gt;top=-1; 
} 
//è¿›æ ˆ
int Push(SeqStack *S,StackElementType x)
{
    if(S-&gt;top==Stack_Size-1)
     return(FALSE);
    S-&gt;top++;
    S-&gt;elem[S-&gt;top]=x;
    return(TRUE);
} 
int Push_num(SeqStack_num *S,StackElementType_num x)
{
    if(S-&gt;top==Stack_Size-1)
     return(FALSE);
    S-&gt;top++;
    S-&gt;elem[S-&gt;top]=x;
    return(TRUE);
} 

//å‡ºæ ˆ
int Pop(SeqStack *S,StackElementType *x)
{
    if(S-&gt;top==-1)
     return(FALSE);
    *x=S-&gt;elem[S-&gt;top];
    S-&gt;top--;
    return(TRUE);
} 
int Pop_num(SeqStack_num *S,StackElementType_num *x)
{
    if(S-&gt;top==-1)
     return(FALSE);
    *x=S-&gt;elem[S-&gt;top];
    S-&gt;top--;
    return(TRUE);
} 

//è¯»æ ˆé¡¶
int GetTop(SeqStack *S,StackElementType *x)
{
    if(S-&gt;top==-1)
     return(FALSE);
    else
    {
        *x=S-&gt;elem[S-&gt;top];
        return(TRUE);
    }
} 

int GetTop_num(SeqStack_num *S,StackElementType_num *x)
{
    if(S-&gt;top==-1)
     return(FALSE);
    else
    {
        *x=S-&gt;elem[S-&gt;top];
        return(TRUE);
    }
} 


//æ¯”è¾ƒä¼˜å…ˆçº§ï¼Œè¿”å›'&gt;','&lt;','=' 
StackElementType Compare(StackElementType ch1,StackElementType ch2)
//ch1ä¸ºè¿˜æœªè¿›æ ˆçš„è¿ç®—ç¬¦ï¼Œch2ä¸ºå½“å‰è¿ç®—ç¬¦æ ˆé¡¶å…ƒç´  
{
    //'('æœªå…¥æ ˆæ—¶ï¼Œä¼˜å…ˆçº§æœ€é«˜ï¼Œå…¥æ ˆåï¼Œä¼˜å…ˆçº§æœ€ä½
    int m,n; 
    switch(ch1)
    {
        case '(':m=6;break;
        case '*':
        case '/':m=5;break;
        case '+':
        case '-':m=4;break;
        case ')':m=3;break;

    }
    switch(ch2)
    {
        case '*':
        case '/':n=6;break;
        case '+':
        case '-':n=5;break;
        case ')':n=4;break;
        case '(':n=3;break;

