    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%c&amp;#34;&lt;/span&gt;,&amp;amp;c);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(c&amp;gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;A&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp; c&amp;lt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;Z&amp;#39;&lt;/span&gt;)
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%c&amp;#34;&lt;/span&gt;,c&amp;#43;&lt;span class="hljs-number"&gt;32&lt;/span&gt;);
