#include &lt;iostream&gt;
#include &lt;random&gt;
#include &lt;time.h&gt;
void main()
{
	char matrix[9][9];
	for (int i = 0; i &lt; 9; i++)
	{
		for (int j = 0; j &lt; 9; j++)
		{
			matrix[i][j] = 0;
		}
	}

	srand(time(NULL));

	int n = 10;
	while (n--)
	{
		int line = rand() % 9;
		int row = rand() % 9;
		if (matrix[line][row] == 0)
		{
			matrix[line][row] = -1;
		}
		else
		{
			n++;
			continue;
		}
	}

	for (int i = 0; i &lt; 9; i++)
	{
		for (int j = 0; j &lt; 9; j++)
		{
			if (matrix[i][j] != -1)
			{
				int leftIndex = j - 1;
				int rightIndex = j + 1;
				int topIndex = i - 1;
				int bottomIndex = i + 1;
				
				int left = leftIndex;
				int top = topIndex;

				int leftMoveStep = 0;
				int topMoveStep = 0;
				int count = 0;
				while (true)
				{
					if (left &gt;= 0 &amp;&amp; left &lt; 9 &amp;&amp; top &gt;= 0 &amp;&amp; top &lt; 9)
					{
						if (matrix[top][left] == -1)
						{ 
							count++;
						}
					}

					if (left == leftIndex &amp;&amp; top == i)
					{
						break;
					}

					if (left - leftIndex &lt; 2 &amp;&amp; top == topIndex)
					{
						leftMoveStep = 1;
						topMoveStep = 0;
					}
					else if (left == rightIndex &amp;&amp; top == topIndex)
					{
						topMoveStep = 1;
						leftMoveStep = 0;
					}
					else if (left == rightIndex &amp;&amp; top == bottomIndex)
					{
						topMoveStep = 0;
						leftMoveStep = -1;
					}
					else if (left == leftIndex &amp;&amp; top == bottomIndex)
					{
						leftMoveStep = 0;
						topMoveStep = -1;
					}
					else if (left - leftIndex &lt; 2 &amp;&amp; top == bottomIndex)
					{
						leftMoveStep = -1;
						topMoveStep = 0;
					}
					left += leftMoveStep;
					top += topMoveStep;
				}

				matrix[i][j] = count;
				
				count &gt; 0 ? printf("%d ", count) : printf("  ");
			}
			else
			{
				printf("%c ", 15);
			}
		}
		printf("\n");
	}

	getchar();
}
