int Date::daysFromOneDay(Date d)
{
    int dif;
    if((*this)==d) return 0;//同天
    if((*this)>d)//在后
    {
        for(dif=0;!((*this)==d);dif++)
            d.plusOne();
        return dif;
    }
    else//在前
    {
        //Date dd(this->getYear(),this->getMonth(),this->getDay());
        Date dd((*this));
        for(dif=0;!(dd==d);dif++)
            dd.plusOne();
        return -dif;
    }
}
