    else return false;
}
//判断日期先后：定义较晚的日期>较早的日期
bool Date::operator>(Date d)
{
    if(getYear()>d.getYear())
        return true;
    else if(getYear()!=d.getYear())
        return false;
    else if(getMonth()>d.getMonth())//同年
        return true;
    else if(getMonth()!=d.getMonth())
        return false;
    else if(getDay()>d.getDay())//同月
        return true;
    else return false;
}
//本月日期
int Date::daysInThisMonth()
{
    switch(getMonth())
    {
        case 4:
        case 6:
        case 9:
        case 11:
            return 30;
        case 2:
            if(isLeap())//二月考虑闰年
                return 29;
            else return 28;
        default:
            return 31;
    }
}
//判断是否闰年
bool Date::isLeap()
{
    int y=getYear();
    if(y % 4 != 0)//不可以被4整除：平年（如2019）
        return false;
    else if(y % 100 != 0)//可以被4整除不可以被100整除：闰年（如2020）
        return true;
    else if(y % 400 != 0)//可以被4和100整除但不能被400整除：平年（如2100）
        return false;
    else return true;//可以被4、100和400整除：闰年（如2000）
}
//加一天
void Date::plusOne()
{
    if(getDay()!=daysInThisMonth())//不是当月最后一天
        day++;
    else if(getMonth()!=12)//是当月最后一天且月份不是12月：日期归一，月份增一
    {
        month++;
        day=1;
    }
    else//是12月31日：日期、月份归一，年份加一
    {
        year++;
        month=1;
        day=1;
    }
}
//计算距某日日期
int Date::daysFromOneDay(Date d)
{
    int dif;
    if((*this)==d) return 0;//同天
    if((*this)>d)//在后
    {
        for(dif=0;!((*this)==d);dif++)
            d.plusOne();
        return dif;
    }
    else//在前
    {
        //Date dd(this->getYear(),this->getMonth(),this->getDay());
        Date dd((*this));
        for(dif=0;!(dd==d);dif++)
            dd.plusOne();
        return -dif;
    }
}
//计算距当前日期
int Date::daysFromNow()
{
    time_t now=time(0);
    tm* ltm=localtime(&now);
    int y=1900+ltm->tm_year;
    int m=1+ltm->tm_mon;
    int d=ltm->tm_mday;
    Date today(y,m,d);
    return daysFromOneDay(today);
}
//显示当前月历
void Date::displayCalendar()
{
    Date std(2020,1,1);
    int week=3;
    if((*this)>std)
    {
        int dif=this->daysFromOneDay(std);
        for(int i=0;i<dif;i++)
        {
            if(week<7)
                week++;
            else week=1;
        }
    }
    else
    {
        int dif=this->daysFromOneDay(std);
        for(int i=0;i>dif;i--)
        {
            if(week>1)
                week--;
            else week=7;
        }
    }
    cout<<getYear()<<"年"<<getMonth()<<"月"<<endl;
    cout<<"日  一  二  三  四  五  六"<<endl;
    int space;
    if(week==7)
        space=0;
    else space=4*week;
    for(int i=0;i<space;i++)
        cout<<" ";
    for(int i=getDay();i<=daysInThisMonth();i++)
    {
        cout<<setw(2)<<i;
        if(week==6)
        {
            cout<<endl;
            week++;
        }
        else if(week==7)
            week=1;
        else week++;
    }
}
