    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;f&lt;/span&gt;(j)&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;)
        {
