    NSLog(@"Retain count is %ld", CFGetRetainCount((__bridge CFTypeRef)label));
    [self.view addSubview:label];
    NSLog(@"Retain count is %ld", CFGetRetainCount((__bridge CFTypeRef)label));
    __weak UILabel *aLabel = label;
