    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; j&amp;lt;i; j&amp;#43;&amp;#43;)
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;strcmp&lt;/span&gt;(m[j].name,str)&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,m[j].number);
}


&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-java"&gt;
&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;Student&lt;/span&gt; {

