import os
import math
class Geometry(object):
    def __init__(self):
        pass
    @property
    def gtype(self):
        return "gtUNKNOWN"

class Point(Geometry):
    def __init__(self,x,y):
        self._x=x
        self._y=y

    @property
    def gtype(self):
        return "gtPoint"

    @property
    def x(self):
        return self._x
    @x.setter
    def x(self,v):
        self._x=v

    @property
    def y(self):
        return self._y
    @y.setter
    def y(self,v):
        self._y=v

    @property
    def length(self):
        return math.sqrt(self._x**2+self._y**2)

    def __str__(self):
        return "Point (%f,%f)"%(self._x,self._y)
    def __repr__(self):
        return self.__str__()

class Circle(Point):
    def __init__(self,x,y,r):
        Point.__init__(self,x,y)
        self._r=r

    @property
    def area(self):
        return math.pi*self._r*self._r

    @property
    def perimeter(self):
        return 2*math.pi*self._r

    @property
    def gtype(self):
        return "gtCircle"

    @property
    def r(self):
        return self._r

    @r.setter
    def r(self,v):
        if v>0:
            self._r=v
        else:
            raise NameError()

    def __str__(self):
        return "Circle(%f,%f,%f)"%(self._x,self._y,self._r)
    def __repr__(self):
        return self.__str__()

class rectangle(Point):
    def __init__(self,x,y,w,h):
        Point.__init__(self,x,y)
        self._w=w
        self._h=h

    @property
    def gtype(self):
        return "gtRectangle"

    @property
    def w(self):
        return self._w
    @w.setter
    def w(self,v):
        self._w=v

    @property
    def h(self):
        return self._h
    @h.setter
    def h(self,v):
        self._h=v

    @property
    def Perimeter(self):
        return 2*self._w+2*self._h

    @property
    def area(self):
        return self._w*self._h
    def __str__(self):
        return "Rectangle(%f,%f,%f,%f)"%(self._x,self._y,self._w,self._h)


