class BROKENLINE(Geometry):
    def __init__(self,x0,y0,x1,y1):
        Geometry.__init__(self)
        self._start=Point(x0,y0)
        self._end=Point(x1,y1)

    @property
    def gtype(self):
        return "gtBROKENLINE"

    @property
    def start(self):
        return Point(self._start.x,self._start.y)
    @start.setter
    def start(self,v):
        self._start.x=v.x
        self._start.y=v.y

    @property
    def end(self):
        return Point(self._end.x,self._end.y)
    @end.setter
    def end(self,v):
        self._end.x=v.x
        self._end.y=v.y

    @property
    def length(self):
        return math.sqrt((self.end.x-self.start.x)**2+(self.end.y-self.start.y)**2)

    def __str__(self):
        return "BROKENLINE(%f,%f,%f,%f)"%(self._start.x,self._start.y,self._end.x,self._end.y)  
    def __repr__(self):
        return self.__str__()

