		for(i = 4; i &gt;= 0; i--)
		{
			a[i] = m % 10;
			m = m / 10;
		}
		if ((a[0] == a[4]) &amp;&amp; (a[1] == a[3])) {
			break;
		}
	}
	printf("%d %d %d %d %d", a[0], a[1], a[2], a[3], a[4]);
}
