&lt;p&gt;#include &amp;lt;stdio.h&amp;gt;&lt;br /&gt;int main()&lt;br /&gt;{&lt;!-- --&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    int i;&lt;/span&gt;&lt;br /&gt;&lt;span style="display:inline-block;text-indent:2em;"&gt;    float f,w;&lt;/span&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-attribute"&gt;f&lt;/span&gt;&amp;#61;&lt;span class="hljs-number"&gt;64&lt;/span&gt;.&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
&lt;span class="hljs-attribute"&gt;w&lt;/span&gt;&amp;#61;(&lt;span class="hljs-number"&gt;5&lt;/span&gt;.&lt;span class="hljs-number"&gt;0&lt;/span&gt;/&lt;span class="hljs-number"&gt;9&lt;/span&gt;)*(f-&lt;span class="hljs-number"&gt;32&lt;/span&gt;);
&lt;span class="hljs-attribute"&gt;i&lt;/span&gt;&amp;#61;&lt;span class="hljs-number"&gt;67&lt;/span&gt;;
&lt;span class="hljs-attribute"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%.2f%c&amp;#34;&lt;/span&gt;,w,i);
