    &lt;span class="hljs-comment"&gt;//菱形&lt;/span&gt;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;选择菱形输出:&amp;#34;&lt;/span&gt;&amp;lt;&amp;lt;endl;
