                t&amp;#43;&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;lt;&lt;span class="hljs-number"&gt;2&lt;/span&gt;*n&lt;span class="hljs-number"&gt;-2&lt;/span&gt;-i )
                {
                    &lt;span class="hljs-built_in"&gt;sprintf&lt;/span&gt;(buf&amp;#43;t,&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,sp);
                    t&amp;#43;&amp;#61;&lt;span class="hljs-built_in"&gt;strlen&lt;/span&gt;(sp);
                }
            }
        }

        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt; (maxl-&lt;span class="hljs-built_in"&gt;strlen&lt;/span&gt;(buf))/&lt;span class="hljs-number"&gt;2&lt;/span&gt;;j&amp;#43;&amp;#43;)
            cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;
        cout &amp;lt;&amp;lt; buf;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt; maxl-(maxl-&lt;span class="hljs-built_in"&gt;strlen&lt;/span&gt;(buf))/&lt;span class="hljs-number"&gt;2&lt;/span&gt;;j&amp;#43;&amp;#43;)
            cout &amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;;
        cout &amp;lt;&amp;lt; endl;

    }


    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;选择三角形输出&amp;#xff1a;&amp;#34;&lt;/span&gt;&amp;lt;&amp;lt;endl;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-number"&gt;2&lt;/span&gt;*n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i&amp;lt;n)
        {
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;&amp;#61;i;j&amp;#43;&amp;#43;)
            {
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;lt;i) cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt; sp;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt; endl;
            }
        }&lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;&lt;span class="hljs-number"&gt;2&lt;/span&gt;*n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;-i;j&amp;#43;&amp;#43;)
            {
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;lt;&lt;span class="hljs-number"&gt;2&lt;/span&gt;*n&lt;span class="hljs-number"&gt;-2&lt;/span&gt;-i)
                    cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt;sp;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
                    cout &amp;lt;&amp;lt; ch&amp;lt;&amp;lt;endl;
            }
        }
    }

    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;选择梯形输出&amp;#xff1a;&amp;#34;&lt;/span&gt;&amp;lt;&amp;lt;endl;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-number"&gt;2&lt;/span&gt;*n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i&amp;lt;n)
        {
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;&amp;#61;i;j&amp;#43;&amp;#43;)
            {
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;lt;i) cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt;sp;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt; endl;
            }
        }&lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        {
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;n;j&amp;#43;&amp;#43;)
            {
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(j&amp;lt;n&lt;span class="hljs-number"&gt;-1&lt;/span&gt;) cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt; sp;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; cout &amp;lt;&amp;lt; ch &amp;lt;&amp;lt; endl;
