        &lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;

        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(s!&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;){  &lt;span class="hljs-comment"&gt;//从s中分解每一位存入到数组arr&lt;/span&gt;
                a&amp;#61;s%&lt;span class="hljs-number"&gt;10&lt;/span&gt;;
                arr[i]&amp;#61;a;
                i&amp;#43;&amp;#43;;
                &lt;span class="hljs-comment"&gt;//System.out.print(a);&lt;/span&gt;
                s&amp;#61;s/&lt;span class="hljs-number"&gt;10&lt;/span&gt;; 
            }

        &lt;span class="hljs-type"&gt;int&lt;/span&gt; j;
        &lt;span class="hljs-type"&gt;int&lt;/span&gt; weight&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;  &lt;span class="hljs-comment"&gt;//每一数位的值要乘以10的几次方&amp;#xff0c;个位是1&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;i-&lt;span class="hljs-number"&gt;1&lt;/span&gt;;j&amp;gt;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j--) {  &lt;span class="hljs-comment"&gt;//访问存储s每一位的数组&amp;#xff0c;把它反转拼接出一个新数字&lt;/span&gt;
            revNum &amp;#43;&amp;#61; arr[j]*weight;
