    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;筛选结果:%s\n&amp;#34;&lt;/span&gt;, a); &lt;span class="hljs-comment"&gt;//输出十六进制的字符&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; count; i&amp;#43;&amp;#43;) {
