    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;number:%d\n&amp;#34;&lt;/span&gt;, r); &lt;span class="hljs-comment"&gt;//输出十进制&lt;/span&gt;
