&lt;p&gt;Here is an example code (playground : &lt;a href="https://play.golang.org/p/izBIq97-0S" rel="nofollow noreferrer"&gt;https://play.golang.org/p/izBIq97-0S&lt;/a&gt;):&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;package main

import (
    "crypto/sha1"
    "encoding/base32"
    "fmt"
    "strings"
)

func main() {
    // Input
    exampleString := "example"

    // SHA1 hash
    hash := sha1.New()
    hash.Write([]byte(exampleString))
    hashBytes := hash.Sum(nil)

    // Conversion to base32
    base32str := strings.ToLower(base32.HexEncoding.EncodeToString(hashBytes))

    fmt.Println(base32str)
}
