&lt;p&gt;Edit : here is my original answer, which reproduces every step from the ruby script, but two of them are unnecessary (playground : &lt;a href="https://play.golang.org/p/tyQt3ftb1j" rel="nofollow noreferrer"&gt;https://play.golang.org/p/tyQt3ftb1j&lt;/a&gt;) :&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;package main

import (
    "crypto/sha1"
    "encoding/base32"
    "encoding/hex"
    "fmt"
    "math/big"
    "strings"
)

func main() {
    // Input
    exampleString := "example"

    // SHA1 hash
    hash := sha1.New()
    hash.Write([]byte(exampleString))
    hashBytes := hash.Sum(nil)

    // Hexadecimal conversion
    hexSha1 := hex.EncodeToString(hashBytes)

    // Integer base16 conversion
    intBase16, success := new(big.Int).SetString(hexSha1, 16)
    if !success {
        panic("Failed parsing big Int from hex")
    }

    // Conversion to base32
    base32str := strings.ToLower(base32.HexEncoding.EncodeToString(intBase16.Bytes()))

    fmt.Println(base32str)
}
