&lt;p&gt;分别记录最大值及其下标、最小值及其下标&amp;#xff0c;然后交换&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; num[&lt;span class="hljs-number"&gt;10&lt;/span&gt;];
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;

    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;n);

    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;num[i]);
    }

    &lt;span class="hljs-type"&gt;int&lt;/span&gt; tmp;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; imax &amp;#61; num[&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; imin &amp;#61; num[&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; idx_max &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; idx_min &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;

    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (num[i] &amp;gt; imax)
        {
            imax &amp;#61; num[i];
            idx_max &amp;#61; i;
        }
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (num[i] &amp;lt; imin)
        {
            imin &amp;#61; num[i];
            idx_min &amp;#61; i;
        }

    }

    tmp &amp;#61; num[n - &lt;span class="hljs-number"&gt;1&lt;/span&gt;];
    num[n - &lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; imax;
    num[idx_max] &amp;#61; tmp;

    tmp &amp;#61; num[&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
    num[&lt;span class="hljs-number"&gt;0&lt;/span&gt;] &amp;#61; imin;
    num[idx_min] &amp;#61; tmp;


    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;, num[i]);

    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

