        &lt;span class="hljs-attr"&gt;xmlns:x&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;http://schemas.microsoft.com/winfx/2006/xaml&amp;#34;&lt;/span&gt;
        &lt;span class="hljs-attr"&gt;Title&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;MainWindow&amp;#34;&lt;/span&gt; &lt;span class="hljs-attr"&gt;Height&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;350&amp;#34;&lt;/span&gt; &lt;span class="hljs-attr"&gt;Width&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;525&amp;#34;&lt;/span&gt;&amp;gt;&lt;/span&gt;
    &lt;span class="hljs-tag"&gt;&amp;lt;&lt;span class="hljs-name"&gt;Grid&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class="hljs-tag"&gt;&amp;lt;&lt;span class="hljs-name"&gt;GroupBox&lt;/span&gt; &lt;span class="hljs-attr"&gt;Header&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;Groupbox&amp;#34;&lt;/span&gt; &lt;span class="hljs-attr"&gt;Visibility&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span class="hljs-template-variable"&gt;{Binding IsVisible, Converter&amp;#61;{StaticResource BooleanToVisibilityConverter}&lt;/span&gt;&lt;span class="language-xml"&gt;&lt;span class="hljs-tag"&gt;&lt;span class="hljs-string"&gt;}&amp;#34;&lt;/span&gt;&amp;gt;&lt;/span&gt;
            &lt;span class="hljs-tag"&gt;&amp;lt;&lt;span class="hljs-name"&gt;StackPanel&lt;/span&gt;&amp;gt;&lt;/span&gt;
                &lt;span class="hljs-tag"&gt;&amp;lt;&lt;span class="hljs-name"&gt;TextBlock&lt;/span&gt; &lt;span class="hljs-attr"&gt;Text&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;Content of Groupbox&amp;#34;&lt;/span&gt; /&amp;gt;&lt;/span&gt;
            &lt;span class="hljs-tag"&gt;&amp;lt;/&lt;span class="hljs-name"&gt;StackPanel&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class="hljs-tag"&gt;&amp;lt;/&lt;span class="hljs-name"&gt;GroupBox&lt;/span&gt;&amp;gt;&lt;/span&gt;
        &lt;span class="hljs-tag"&gt;&amp;lt;&lt;span class="hljs-name"&gt;Button&lt;/span&gt; &lt;span class="hljs-attr"&gt;Content&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;Toggle Groupbox Visibility&amp;#34;&lt;/span&gt; &lt;span class="hljs-attr"&gt;Click&lt;/span&gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#34;Button_Click&amp;#34;&lt;/span&gt;/&amp;gt;&lt;/span&gt;
    &lt;span class="hljs-tag"&gt;&amp;lt;/&lt;span class="hljs-name"&gt;Grid&lt;/span&gt;&amp;gt;&lt;/span&gt;
&lt;span class="hljs-tag"&gt;&amp;lt;/&lt;span class="hljs-name"&gt;Window&lt;/span&gt;&amp;gt;&lt;/span&gt;


&lt;/span&gt;
&lt;/code&gt;&lt;/pre&gt;
