&lt;pre&gt;&lt;code class="language-sql"&gt;&lt;span class="hljs-keyword"&gt;select&lt;/span&gt; g ,min(d) date_from,max(d) date_to,sum(val) sum_val &lt;span class="hljs-keyword"&gt;from&lt;/span&gt; (
&lt;span class="hljs-keyword"&gt;select&lt;/span&gt; round(((row_number() &lt;span class="hljs-keyword"&gt;over&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;order&lt;/span&gt; &lt;span class="hljs-keyword"&gt;by&lt;/span&gt; d))&lt;span class="hljs-number"&gt;-1&lt;/span&gt;)/&lt;span class="hljs-number"&gt;5&lt;/span&gt;,&lt;span class="hljs-number"&gt;0&lt;/span&gt;,&lt;span class="hljs-number"&gt;1&lt;/span&gt;) g,a.*  &lt;span class="hljs-keyword"&gt;from&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;select&lt;/span&gt; d ,sum(val) val test_table &lt;span class="hljs-keyword"&gt;group&lt;/span&gt; &lt;span class="hljs-keyword"&gt;by&lt;/span&gt; d) a) &lt;span class="hljs-keyword"&gt;as&lt;/span&gt; t
&lt;span class="hljs-keyword"&gt;group&lt;/span&gt; &lt;span class="hljs-keyword"&gt;by&lt;/span&gt; g
&lt;/code&gt;&lt;/pre&gt;

