using namespace std;
int max(int a,int b) {
	if (a&gt;=b)
		return a;
	else if (a&lt;b)
		return b; 
}
int max(int a,int b,int c){
	int d;{
		if(a&gt;=b)
			d=a;
		else if(a&lt;b)
			d=b;}
	if(d&gt;=c)
		return d;
	else if(d&lt;c) 
		return c;
} 
double max(double a,double b){ 
	if (a&gt;b)
		return a;
	else if (a&lt;=b)
		return b; 
} 
double max(double a,double b,double c){
	double d;{
		if(a&gt;b)
			d=a;
		else if(a&lt;=b)
			d=b;}
	if(d&gt;c)
		return d;
	else if(d&lt;=c) 
		return c;
}
