#include &lt;iostream&gt;

using namespace std;

template &lt;typename T, int size&gt;
T max(T a, ...)
{
	T * p = &amp;a;
	T max = a;
	for (int i = 0; i &lt; size; i++)
        if (max &lt; p[i]) max = p[i];
    return max;
}

int main()
{
	cout &lt;&lt; max&lt;int, 3&gt;(1,2,3) &lt;&lt; endl;
	cout &lt;&lt; max&lt;double, 5&gt;(1.2,2.7,1.3,5.9,4.4) &lt;&lt; endl;
	return 0;
}
