&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;System&lt;/span&gt;.Data;
namespace ConsoleApp1
{
    &lt;span class="hljs-keyword"&gt;class&lt;/span&gt; Program
    {
        static &lt;span class="hljs-type"&gt;void&lt;/span&gt; Main(string[] args)
        {
            string &lt;span class="hljs-keyword"&gt;sql&lt;/span&gt; &amp;#61; &amp;#34;insert into data(username,sex,age)values(?,?,?)&amp;#34;;
            OleDbConnection conn &amp;#61; &lt;span class="hljs-built_in"&gt;new&lt;/span&gt; OleDbConnection(&amp;#64;&amp;#34;provider&amp;#61;microsoft.ace.oledb.12.0;data source&amp;#61;D:\文件\C#\Console\ConsoleApp1\bin\Debug\data.accdb&amp;#34;);
            conn.&lt;span class="hljs-keyword"&gt;Open&lt;/span&gt;();
            OleDbCommand cmd &amp;#61; &lt;span class="hljs-built_in"&gt;new&lt;/span&gt; OleDbCommand(&lt;span class="hljs-keyword"&gt;sql&lt;/span&gt;, conn);
            //注意&lt;span class="hljs-keyword"&gt;access&lt;/span&gt;参数化传入的参数是按照参数出现顺序来添加&amp;#xff0c;顺序能乱&amp;#xff0c;名称随便起&amp;#xff0c;一样也无所谓
            cmd.Parameters.&lt;span class="hljs-keyword"&gt;Add&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;new&lt;/span&gt; OleDbParameter() { ParameterName &amp;#61; &amp;#34;?&amp;#34;, OleDbType &amp;#61; OleDbType.VarWChar, Size &amp;#61; &lt;span class="hljs-number"&gt;20&lt;/span&gt;, &lt;span class="hljs-keyword"&gt;Value&lt;/span&gt; &amp;#61; &amp;#34;username的值&amp;#34; });
            cmd.Parameters.&lt;span class="hljs-keyword"&gt;Add&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;new&lt;/span&gt; OleDbParameter() { ParameterName &amp;#61; &amp;#34;p&amp;#34;, OleDbType &amp;#61; OleDbType.VarWChar, Size &amp;#61; &lt;span class="hljs-number"&gt;20&lt;/span&gt;, &lt;span class="hljs-keyword"&gt;Value&lt;/span&gt; &amp;#61; &amp;#34;sex的值&amp;#34; });
            cmd.Parameters.&lt;span class="hljs-keyword"&gt;Add&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;new&lt;/span&gt; OleDbParameter() { ParameterName &amp;#61; &amp;#34;p&amp;#34;, OleDbType &amp;#61; OleDbType.Integer, &lt;span class="hljs-keyword"&gt;Value&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;123&lt;/span&gt; });
            cmd.ExecuteNonQuery();
            conn.&lt;span class="hljs-keyword"&gt;Close&lt;/span&gt;();
            Console.ReadKey();
        }
    }
}


