template&lt;class T,class U&gt;
int Compare(T a, U b)
{
	if (a&gt;b)
	{
		return 1;
	}
   if (a == b)
	{
		return 0;
	}
   if (a &lt; b) 
		return -1;
}

void MInt()
{
	int a = 3;
	int b = 5;
	if (Compare&lt;int, int&gt;(a, b) ==0)
		cout &lt;&lt; "a == b" &lt;&lt; endl;
	else if (Compare&lt;int, int&gt;(a, b) &gt; 0)
	{
		cout &lt;&lt; "a &gt; b" &lt;&lt; endl;
	}
	else if (Compare&lt;int, int&gt;(a, b) &lt; 0) 
		cout &lt;&lt; "a &lt; b" &lt;&lt; endl;
}

void MDouble()
{
	double a = 4.0;
	double b = 5.0;
	if (Compare&lt;double, double&gt;(a, b) == 0)
		cout &lt;&lt; "a == b" &lt;&lt; endl;
	else if (Compare&lt;double, double&gt;(a, b) &gt; 0)
