    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;,flag&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;double&lt;/span&gt; sum&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;,eps;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%lf&amp;#34;&lt;/span&gt;,&amp;amp;eps);
