function to_array($unknown) {
  if (count($unknown) === 0) {
    $array=array($unknown);
  } elseif (!is_array($unknown)) {
    $array = array();
    $array[0] = $unknown;
  } else {
    $array = $unknown;
  }
  return $array;
}

